sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

//     alert("Loading is " + isLoading);
//     alert("newValue is " + newValue);
//     alert("oldValue is " + oldValue);
//     alert("NewRecord is " + g_form.isNewRecord());

    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    //  (サーバーへの不必要なアクセスを避けるために、newValue == oldValue の場合に返すように if を変更しました。)
    //     if (isLoading || newValue === '' || newValue == oldValue) {
    //         return;
    //     }
    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
        return;
    }
//     alert("");

    // Instantiate the GetEmailAddress Script Include  (GetEmailAddress スクリプト インクルードをインスタンス化します。)
    var getEmailAddr = new GlideAjax('GetEmailAddress');
    // Specify the getEmail method  (getEmailメソッドを指定する)
    getEmailAddr.addParam('sysparm_name', 'getEmail');
    // Pass the Requested for sys_id  (要求された sys_id を渡します)
    getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
    // Send the request to the server  (リクエストをサーバーに送信する)
    getEmailAddr.getXML(populateEmailField);


    // When the response is back from the server  (サーバーからレスポンスが戻ってきたら)
    function populateEmailField(response) {
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        // 	(応答から電子メール アドレスを抽出し、電子メール フィールドから値をクリアし、電子メール フィールドに新しい値を設定します。)
        var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('u_requested_for_email');
        g_form.setValue('u_requested_for_email', emailFromScriptInclude);
    }
}
